var clock={secondHand:null,minHand:null,hoursdHand:null,renderDOM:function(el){var target=document.getElementById(el);console.log(target,el);target.innerHTML=`<div class="clock"><div class="clock-face"><div class="text tw">12</div><div class="text th">3</div><div class="text sx">6</div><div class="text n">9</div><div class="hand hours"></div><div class="hand min"></div><div class="hand sec"></div></div></div>`;this.secondHand=document.querySelector(".sec");this.minHand=document.querySelector(".min");this.hoursdHand=document.querySelector(".hours");return!0},clockMe:function(options){this.renderDOM(options.el);let e=this;setInterval(function(){let t=new Date,n=t.getSeconds()/60*360+90;e.secondHand.style.transform="rotate("+n+"deg)";let o=t.getMinutes()/60*360+90;e.minHand.style.transform="rotate("+o+"deg)";let r=t.getHours()/12*360+90;e.hoursdHand.style.transform="rotate("+r+"deg)"},1e3)}}